document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[data-tabs]").forEach(e=>{const t=e.querySelectorAll("[data-tab-button]"),n=e.querySelectorAll("[data-tab-panel]");t.length>0&&n.length>0&&(t[0].classList.add("tab-active"),n[0].classList.add("tab-active")),t.forEach((e,s)=>{e.addEventListener("click",()=>{t.forEach(e=>e.classList.remove("tab-active")),n.forEach(e=>e.classList.remove("tab-active")),e.classList.add("tab-active"),n[s].classList.add("tab-active")})})})})